    !徐士良书上的子程序(C=AB)
    SUBROUTINE BRMUL(A,B,M,N,K,C)
    
    implicit none
    
    integer,intent(in)::M,N,K                                      !输入数组维度n*n
    real(kind=8),dimension(M,N),intent(in)::A                      !输入数组名称
    real(kind=8),dimension(N,K),intent(in)::B                      !输入数组名称
    real(kind=8),dimension(M,K),intent(out)::C                     !输出数组名

    integer::I,J,L
    
    DO 50 I=1,M
    DO 50 J=1,K
      C(I,J)=0.0
      DO 10 L=1,N
        C(I,J)=C(I,J)+A(I,L)*B(L,J)
10    CONTINUE
50  CONTINUE
    RETURN
    END SUBROUTINE BRMUL
