    !**********************************************************************************
    !经检验正确---免检
    !倾斜角theta为锐角(逆时针)free-slip边界条件，切向速度相同
    !坐标转换时是从一个坐标的正方向转换到另一个坐标上，再从另一个坐标的正方形转换回来，中间不要换方向
    subroutine cline_free_slip(particle_velocity_x,particle_velocity_y,beta,ghost_velocity_x,ghost_velocity_y)

    implicit none

    ! Variables
          
    integer::i,j,k,l                                                       !循环控制变量
    real(kind=8),intent(in)::particle_velocity_x,particle_velocity_y       !输入流域内实粒子速度
    real(kind=8),intent(in)::beta                                          !输入斜板的倾斜角
    real(kind=8),intent(out)::ghost_velocity_x,ghost_velocity_y            !输出固定镜像粒子速度

    !子程序内部变量
    real(kind=8)::particle_u,particle_v                                    !流域内沿斜板切向和垂向速度
    real(kind=8)::ghost_u,ghost_v                                          !固定镜像粒子沿斜板切向和垂向速度
    real(kind=8)::theta                                                    !弧度制角度
    
    !body of subroutine cline_free_slip
    !将角度值倾斜角转换为弧度制倾斜角
    theta=beta*PI/180.0
    
    !将正交坐标系下转化为斜坐标系（这个是不变的）
    particle_u=particle_velocity_x*cos(theta)+particle_velocity_y*sin(theta)
    particle_v=-particle_velocity_x*sin(theta)+particle_velocity_y*cos(theta)
    
    !自由滑移边界条件
    ghost_u=particle_u
    ghost_v=-particle_v
    
    !将斜坐标系下转为正交坐标系，输出固定镜像粒子的水平和垂向速度（这个是不变的）
    ghost_velocity_x=ghost_u*cos(theta)-ghost_v*sin(theta)
    ghost_velocity_y=ghost_u*sin(theta)+ghost_v*cos(theta)
    
    end subroutine cline_free_slip
